import { uploadPhoto, createUser } from './utils.js';

function handleProfileSignup() {
  // Create an array of promises
  const promises = [
    uploadPhoto(),
    createUser(),
  ];

  // Use Promise.all to collectively resolve all promises
  Promise.all(promises)
    .then((results) => {
      // results will contain the resolved values from each promise
      const [photoResponse, userResponse] = results;

      // Extract relevant data from the responses
      const { body: photoBody } = photoResponse;
      const { body: userBody } = userResponse;

      // Log the required data to the console
      console.log(`firstName ${userBody.firstName} lastName ${userBody.lastName}`);
    })
    .catch((error) => {
      // Log an error message in case of an error
      console.error('Signup system offline');
    });
}

export default handleProfileSignup;

